import React from 'react'
import { Spinner } from 'react-bootstrap';
import { useQuery } from 'react-query';
import { RouteComponentProps, useParams } from 'react-router-dom';

interface Props {
    setAuth: (isAuthenticated: boolean) => void;
}

type OrderItemType = {
    date: string;
    productDetail: ProductDetailType;
    deliveryCardDetails: deliveryCardDetailType;
}

type ProductDetailType = {
    pname : string;
    quantity: number;
    pricesold: number;
}

type deliveryCardDetailType = {
    shippingaddress: string,
    name: string,
    cardtype: string,
    billingaddress: string
}


const OrderScreenItem = ({ setAuth }: Props) => {
    const {id, date} = useParams<{id?: string, date?: string}>();
    const getOrder = async (): Promise<OrderItemType> => await (await fetch(`http://localhost:8000/orders/${id}/${date}`)).json();

    const { data, isLoading, error } = useQuery<OrderItemType>('order', getOrder);

    if (isLoading) {
        return (
          <div className='d-flex justify-content-center align-items-center' style={{ height: '75vh' }}>
            <Spinner animation="border" />
          </div>
        )
      }
      if (error) return <div>Something went wrong...</div>
    console.log(date);
    return (
        <div>
            <h3>{data?.date}</h3>
            <h3>{data?.productDetail.pname}</h3>
            <h3>{data?.productDetail.pricesold}</h3>
            {/* <h3>{status}</h3> */}
        </div>
    )
}

export default OrderScreenItem;
