import React from 'react'
import { Accordion, ListGroup, Spinner } from 'react-bootstrap';
import { useQuery } from 'react-query';
import { RouteComponentProps, useParams } from 'react-router-dom';

interface Props {
    setAuth: (isAuthenticated: boolean) => void;
}

type OrderItemType = {
    date: string;
    productDetails: ProductDetailType[];
    deliveryCardDetails: deliveryCardDetailType;
}

type ProductDetailType = {
    pname: string;
    quantity: number;
    pricesold: number;
}

type deliveryCardDetailType = {
    shippingaddress: string,
    name: string,
    cardtype: string,
    billingaddress: string
}


const OrderScreenItem = ({ setAuth }: Props) => {
    const { id, date } = useParams<{ id?: string, date?: string }>();
    const getOrder = async (): Promise<OrderItemType> => await (await fetch(`http://localhost:8000/orders/${id}/${date}`)).json();

    const { data, isLoading, error } = useQuery<OrderItemType>('order', getOrder);

    if (isLoading) {
        return (
            <div className='d-flex justify-content-center align-items-center' style={{ height: '75vh' }}>
                <Spinner animation="border" />
            </div>
        )
    }
    if (error) return <div>Something went wrong...</div>
    console.log(data);
    return (
        <>
            <Accordion defaultActiveKey="0">
                <Accordion.Item eventKey="0">
                    <Accordion.Header>Card Details</Accordion.Header>
                    <Accordion.Body>
                        <ListGroup>
                            <ListGroup.Item>Card Name : ${data?.deliveryCardDetails.name}</ListGroup.Item>
                            <ListGroup.Item>Card Provider : {data?.deliveryCardDetails.cardtype}</ListGroup.Item>
                            <ListGroup.Item>Shipping Address : {data?.deliveryCardDetails.billingaddress}</ListGroup.Item>
                        </ListGroup>
                    </Accordion.Body>
                </Accordion.Item>
                <Accordion.Item eventKey="1">
                    <Accordion.Header>Shipping Details</Accordion.Header>
                    <Accordion.Body>
                        Shipping Address : {data?.deliveryCardDetails.shippingaddress}
                    </Accordion.Body>
                </Accordion.Item>
            </Accordion>
        </>
    )
}

export default OrderScreenItem;
