import React, { useState } from 'react'
import { Badge, Card, ListGroup, Spinner } from 'react-bootstrap';
import { useQuery } from 'react-query';

interface Props {
  setAuth: (isAuthenticated: boolean) => void;
}

export type OrderType = {
  id: number;
  totalAmount: number;
  date: Date;
  status: string;
}

const getOrders = async (): Promise<OrderType[]> => await (await fetch('http://localhost:8000/orders')).json();

const OrderScreen = ({ setAuth }: Props) => {
  const [orderList, setOrderList] = useState([] as OrderType[]);
  const { data, isLoading, error } = useQuery<OrderType[]>('orders', getOrders);

  console.log(data);
  if (isLoading) {
    return (
      <div className='d-flex justify-content-center align-items-center' style={{ height: '75vh' }}>
        <Spinner animation="border" />
      </div>
    )
  }
  if (error) return <div>Something went wrong...</div>


  return (
    <ListGroup>
      <ListGroup.Item
        as="li"
        className="d-flex justify-content-between align-items-start"
      >
        {orderList.map(order => (
          <Card.Body>
            <Card.Title>${order.totalAmount}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">25th September, 2022</Card.Subtitle>
            <Card.Text className=''>
              Some quick example text to build on the card title and make up the
              bulk of the card's content.
            </Card.Text>
            if (order.status==='Completed') {
              <Badge bg="success">Success</Badge>
            } else {
              <Badge bg="warning">Pending</Badge>
            }
            <Card.Link href="/${order.id}">View More</Card.Link>
          </Card.Body>
        ))}
      </ListGroup.Item>
    </ListGroup>
  )
}

export default OrderScreen;
