import React, { useState } from 'react'
import { Collapse } from 'react-bootstrap';
import { Drawer } from 'react-bootstrap-drawer';
import { CartItemType } from '../../screens/StoreScreen/StoreScreen';

function CartDrawer() {
    const [showCart, setShowCart] = useState(true); // state to track whether the cart is open
    const [cartItems, setCartItems] = useState<CartItemType[]>([]); // state to track the items in the cart

    const handleAddToCart = (item: CartItemType) => {
        // add the item to the cart
        setCartItems([...cartItems, item]);
    };

    const handleCloseCart = () => {
        // close the cart
        setShowCart(false);
    };



    return (
        <>
            <button onClick={() => setShowCart(true)}>View Cart</button>
            <Drawer show={showCart} onHide={handleCloseCart}>
                <div>
                    <h2>Shopping Cart</h2>
                    <ul>
                        {cartItems.map(item => (
                            <li key={item.id}>
                                {item.title} ({item.amount}) - ${item.price * item.amount}
                            </li>
                        ))}
                    </ul>
                </div>
            </Drawer>
        </>

    )
}

export default CartDrawer;
