import React, { SyntheticEvent } from 'react'
import Button from 'react-bootstrap/esm/Button';
import CartItem from '../../components/CartItem/CartItem';
import { CartItemType } from '../StoreScreen/StoreScreen'

interface Props {
    cartItems: CartItemType[],
    setAuth: (isAuthenticated: boolean) => void;
}

function CheckoutScreen({cartItems}: Props) {
  const getTotalItems = (items: CartItemType[]) => items.reduce((acc: number, item) => acc + item.amount, 0);
  const payNowHandler = (e: SyntheticEvent, cartItems: CartItemType[]) => { console.log(e, cartItems)};
  return (
    <>
    {cartItems.map(item => (
      <CartItem
        key={item.id}
        item={item}
        addToCart={() => {}}
        removeFromCart={() => {}}
      />
    ))}
    <h2>Total: ${getTotalItems(cartItems).toFixed(2)}</h2>
    <ul>
      {cartItems.map(item => (
        <li key={item.id}>
          {item.title} ({item.amount}) - ${item.price * item.amount}
        </li>
      ))}
    </ul>
    <Button variant="dark" disabled={cartItems.length === 0} onClick={(e) => payNowHandler(e, cartItems)}>Pay</Button>
    </>
  )
}

export default CheckoutScreen
