import React from 'react'
import { Badge, Card, Form, ListGroup, Spinner } from 'react-bootstrap';
import { useQuery } from 'react-query';


type TransactionType = {
  transactionid: number;
  tstatus: string;
  tdate: string;
  totalamount: number;
}

const getTransactions = async (): Promise<TransactionType[]> => {
  const token = localStorage.getItem('token');
  const header = { 'token': `${token}` };
  return await (await fetch('http://localhost:8000/transactions', { headers: header })).json();
}


const TransactionScreen = () => {
  const { data, isLoading, error } = useQuery<TransactionType[]>('transactions', getTransactions);

  if (isLoading) {
    return (
      <div className='d-flex justify-content-center align-items-center' style={{ height: '75vh' }}>
        <Spinner animation="border" />
      </div>
    )
  }
  if (error) return <div>Something went wrong...</div>


  return (
    <>
      <h2>Filters</h2>
      <Form onSubmit={submitHandler}>
        <div className="mb-3" >
          <Form.Check
            type='radio'
            id='1'
            label='Highest Sold Products'
            onChange={(e) => setMetric('highestSold')}
            checked={metric === 'highestSold'}
          />

          <Form.Check
            type='radio'
            id='2'
            label='Highest Sold Products to Distinct Customers'
            onChange={(e) => setMetric('highestSoldCustom')}
            checked={metric === 'highestSoldCustom'}
          />

          <Form.Check
            type='radio'
            id='3'
            label='Top 10 customers'
            onChange={(e) => setMetric('topTenCustom')}
            checked={metric === 'topTenCustom'}
          />

          <Form.Group className="mb-3" controlId="startDate">
            <Form.Label>Product Name</Form.Label>
            <Form.Control type="date" placeholder="Enter product name" value={startDate}
              onChange={(e) => setStartDate(e.target.value)} />
          </Form.Group>

          <Form.Group className="mb-3" controlId="endDate">
            <Form.Label>End Date</Form.Label>
            <Form.Control type="date" placeholder="endDate" value={endDate}
              onChange={(e) => setEndDate(e.target.value)} />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </div>
      </Form>
      <ListGroup>
        <ListGroup.Item
          as="li"
        >
          {data?.map(transaction => (
            <Card.Body className='d-block' style={{ margin: '1rem' }}>
              <div className='d-flex justify-content-between'>
                <Card.Title>${transaction.totalamount}</Card.Title>
                {transaction.tstatus === 'Completed' ? <Badge bg="success">Success</Badge> : <Badge bg="warning">Pending</Badge>}
              </div>
              <Card.Subtitle className="mb-2 text-muted">${transaction.tdate}</Card.Subtitle>
              {/* <div>
              <Card.Link href={`/transaction/${transaction.transactionid}`}>View More</Card.Link>
            </div> */}
            </Card.Body>
          ))}
        </ListGroup.Item>
      </ListGroup>
    </>
  )
}

export default TransactionScreen;
