import React, { SyntheticEvent, useState } from 'react'
import { Form } from 'react-bootstrap';
import Button from 'react-bootstrap/esm/Button';
import CartItem from '../../components/CartItem/CartItem';
import { CartItemType } from '../StoreScreen/StoreScreen'


const payNowHandler = async (e: SyntheticEvent, cartItems: CartItemType[]) => {
  const token = localStorage.getItem('token')
  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json', 'token': `${token}` },
    body: JSON.stringify({ cartItems })
  };

  const response = await fetch('https://localhost:8000/payment', requestOptions);
  const res = await response.json();
  console.log(res);
}

function CheckoutScreen() {
  const [cardNumber, setCardNumber] = useState('');
  const [secNumber, setSecNumber] = useState('');
  const [cardOwnerName, setCardOwnerName] = useState('');
  const [lastName, setLastName] = useState('');
  const [redirectToLogin, setRedirectToLogin] = useState(false);
  const getTotalItems = (items: CartItemType[]) => items.reduce((acc: number, item) => acc + item.amount, 0);
  const payNowHandler = (e: SyntheticEvent, cartItems: CartItemType[]) => {
    console.log(e, cartItems)
  };
  const localStorageObj = localStorage.getItem('cart-data') ? localStorage.getItem('cart-data') : "";
  const cartItems = JSON.parse(localStorageObj ? localStorageObj : "") as CartItemType[];

  if (cartItems.length) {
    return (
      <>
        <ul>
          {cartItems.map(item => (
            <li key={item.id}>
              <img src={item.image} style={{ width: '100px', height: '100px' }} />
              {item.title} ({item.amount}) = ${item.price * item.amount}
            </li>
          ))}
        </ul>
        <h2>Total: ${getTotalItems(cartItems).toFixed(2)}</h2>
        <FormContainer>
          <Form className="my-3" onSubmit={submitHandler}>
            <Form.Group className="mb-3" controlId="firstName">
              <Form.Label>Credit Card Number</Form.Label>
              <Form.Control type="firstName" placeholder="Enter your first name"
                value={cardNumber}
                onChange={(e) => setFirstName(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="lastName">
              <Form.Label>Last Name</Form.Label>
              <Form.Control type="lastName" placeholder="Enter your last name"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="email">
              <Form.Label>Email Address</Form.Label>
              <Form.Control type="email" placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)} />
            </Form.Group>
            <Form.Group className="mb-3" controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)} />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </FormContainer>
        <Button variant="dark" disabled={cartItems.length === 0} onClick={(e) => payNowHandler(e, cartItems)}>Pay</Button>
      </>
    )
  } else {
    return <h1>Empty Cart</h1>
  }

}

export default CheckoutScreen
